import React from "react";

// import FormInput from "../../components/form-input/form-input.component.jsx";
import CustomButton from "../../components/custom-button/custom-button.component.jsx";
import FormInput2 from "../../components/form-input/form-input2";
import "../../pages/doctor-portal/AdminLogInPage.scss";

import "../../components/sign-in/sign-in.styles.scss";

class DoctorPortal extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
    };
  }

  handleSubmit = (event) => {
    event.preventDefault();

    this.setState({ email: "", password: "" });
  };

  handleChange = (event) => {
    const { value, name } = event.target;

    this.setState({ [name]: value });
  };

  render() {
    return (
      <div>
        <div className="admin-login-page">
          <h1 className="title">Employee Absence Management System</h1>
          <div className="login-page-form-container">
            <h2 className="sub-title">Admin Sign In</h2>
            <span>Please log in with your admin credentials</span>
            <form onSubmit={this.handleSubmit}>
              <FormInput2
                type="email"
                value={this.admin.email}
                name="email"
                onChange={this.handleChange}
                id="email"
                label="Email"
                required
              />
              <FormInput2
                type="password"
                value={this.admin.password}
                name="password"
                onChange={this.handleChange}
                id="password"
                label="Password"
                required
              />
              <CustomButton type="submit">SIGN IN</CustomButton>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default DoctorPortal;
