import React, { useState } from "react";
import "../doctor-portal/AdminLogInPage.scss";
import FormInput2 from "../../components/form-input/form-input2.scss";
import CustomButton from "../../components/custom-button/custom-buttom.styles.scss";

const DoctorPortal = () => {
  const [admin, setAdmin] = useState({ email: "", password: "" });

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setAdmin({ ...admin, [name]: value });
  };

  return (
    <div>
      <div className="admin-login-page">
        <h1 className="title">Employee Absence Management System</h1>
        <div className="login-page-form-container">
          <h2 className="sub-title">Admin Sign In</h2>
          <span>Please log in with your admin credentials</span>
          <form onSubmit={handleSubmit}>
            <FormInput2
              type="email"
              value={admin.email}
              name="email"
              onChange={handleChange}
              id="email"
              label="Email"
              required
            />
            <FormInput2
              type="password"
              value={admin.password}
              name="password"
              onChange={handleChange}
              id="password"
              label="Password"
              required
            />
            <CustomButton type="submit">SIGN IN</CustomButton>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DoctorPortal;
